    .Bits(16)
  )
  Mux_2x1_NBits_i0 (
    .sel( s3 ),
    .in_0( R ),
    .in_1( s4 ),
    .out( s5 )
  );
  // eq
  CompUnsigned #(
    .Bits(16)
  )
  CompUnsigned_i1 (
    .a( \xor  ),
    .b( 16'b0 ),
    .\= ( ne )
  );
  assign eq = ~ ne;
  DIG_Add #(
    .Bits(16)
  )
  DIG_Add_i2 (
    .a( L ),
    .b( s5 ),
    .c_i( s3 ),
    .s( s0 ),
    .c_o( s6 )
  );
  Mux_8x1_NBits #(
    .Bits(16)
  )
  Mux_8x1_NBits_i3 (
    .sel( op ),
    .in_0( 16'b0 ),
    .in_1( s0 ),
    .in_2( s0 ),
    .in_3( 16'b0 ),
    .in_4( R ),
    .in_5( \xor  ),
    .in_6( s1 ),
    .in_7( s2 ),
    .out( result )
  );
  assign hs = ~ s6;
  assign ge = (s0[15] ^ ((L[15] & s5[15] & ~ s0[15]) | (~ L[15] & ~ s5[15] & s0[15])));
  assign lt = ~ ge;
  Mux_8x1 Mux_8x1_i4 (
    .sel( cond ),
    .in_0( 1'b0 ),
    .in_1( eq ),
    .in_2( ne ),
    .in_3( lt ),
    .in_4( ge ),
    .in_5( s6 ),
    .in_6( hs ),
    .in_7( 1'b0 ),
    .out( skip_n )
  );
  assign L_out = L;
endmodule
module DIG_RegisterFile
#(
    parameter Bits = 8,
    parameter AddrBits = 4
)
(
    input [(Bits-1):0] Din,
    input we,
    input [(AddrBits-1):0] Rw,
    input C,
    input [(AddrBits-1):0] Ra,
    input [(AddrBits-1):0] Rb,
    output [(Bits-1):0] Da,
    output [(Bits-1):0] Db
);

    reg [(Bits-1):0] memory[0:((1 << AddrBits)-1)];

    assign Da = memory[Ra];
    assign Db = memory[Rb];

    always @ (posedge C) begin
        if (we)
            memory[Rw] <= Din;
    end
endmodule


module regfile (
  input [3:0] rs,
  input [3:0] rd,
  input [15:0] wrval,
  input clock,
  input en,
  input wr,
  output [15:0] rdval,
  output [15:0] rsval
);
  wire s0;
  assign s0 = (en & wr);
  DIG_RegisterFile #(
    .Bits(16),
    .AddrBits(4)
  )
  DIG_RegisterFile_i0 (
    .Din( wrval ),
    .we( s0 ),
    .Rw( rd ),
    .C( clock ),
    .Ra( rd ),
    .Rb( rs ),
    .Da( rdval ),
    .Db( rsval )
  );
endmodule
module DIG_RAMDualAccess
#(
    parameter Bits = 8,
    parameter AddrBits = 4
)
(
    input C, // Clock signal
    input ld,
    input [(AddrBits-1):0] \1A ,
    input [(AddrBits-1):0] \2A ,
    input [(Bits-1):0] \1Din ,
    input str,
    output [(Bits-1):0] \1D ,
    output [(Bits-1):0] \2D
);
    // CAUTION: uses distributed RAM
    reg [(Bits-1):0] memory [0:((1 << AddrBits)-1)];

    assign \1D = ld? memory[\1A ] : 'hz;
    assign \2D = memory[\2A ];

    always @ (posedge C) begin
        if (str)
            memory[\1A ] <= \1Din ;
    end

endmodule



module debugregs (
  input [15:0] data_in,
  input valid_in,
  input [7:0] addr_in,
  input ready_in,
  input write,
  input en,
  input clock,
  input [3:0] rd,
  input [15:0] wrval,
  input [7:0] sel,
  output [15:0] data_out,
  output valid_out,
  output [7:0] addr_out,
  output ready_out
);
  wire s0;
  wire [3:0] s1;
  wire [15:0] s2;
  wire [3:0] s3;
  wire [3:0] s4;
  wire s5;
  assign s0 = (en & write);
  assign s1 = addr_in[3:0];
  assign s3 = addr_in[7:4];
  assign s4 = sel[7:4];
  DIG_RAMDualAccess #(
    .Bits(16),
    .AddrBits(4)
  )
  DIG_RAMDualAccess_i0 (
    .str( s0 ),
    .C( clock ),
    .ld( 1'b0 ),
    .\1A ( rd ),
    .\1Din ( wrval ),
    .\2A ( s1 ),
    .\2D ( s2 )
  );
  // active?
  CompUnsigned #(
    .Bits(4)
  )
  CompUnsigned_i1 (
    .a( s3 ),
    .b( s4 ),
    .\= ( s5 )
  );
  Mux_2x1_NBits #(
    .Bits(16)
  )
  Mux_2x1_NBits_i2 (
    .sel( s5 ),
    .in_0( data_in ),
    .in_1( s2 ),
    .out( data_out )
  );
  assign valid_out = (s5 | valid_in);
  assign addr_out = addr_in;
  assign ready_out = ready_in;
endmodule

module debugmux (
  input [15:0] data_in,
  input valid_in,
  input [7:0] addr_in,
  input ready_in,
  input [7:0] sel,
  input [15:0] in_0,
  input [15:0] in_1,
  input [15:0] in_2,
  input [15:0] in_3,
  input [15:0] in_4,
  input [15:0] in_5,
  input [15:0] in_6,
  input [15:0] in_7,
  output [15:0] data_out,
  output valid_out,
  output [7:0] addr_out,
  output ready_out
);
  wire [4:0] s0;
  wire [4:0] s1;
  wire s2;
  wire [2:0] s3;
  wire [15:0] s4;
  assign s3 = addr_in[2:0];
  assign s0 = addr_in[7:3];
  assign s1 = sel[7:3];
  // active?
  CompUnsigned #(
    .Bits(5)
  )
  CompUnsigned_i0 (
    .a( s0 ),
    .b( s1 ),
    .\= ( s2 )
  );
  Mux_8x1_NBits #(
    .Bits(16)
  )
  Mux_8x1_NBits_i1 (
    .sel( s3 ),
    .in_0( in_0 ),
    .in_1( in_1 ),
    .in_2( in_2 ),
    .in_3( in_3 ),
    .in_4( in_4 ),
    .in_5( in_5 ),
    .in_6( in_6 ),
    .in_7( in_7 ),
    .out( s4 )
  );
  Mux_2x1_NBits #(
    .Bits(16)
  )
  Mux_2x1_NBits_i2 (
    .sel( s2 ),
    .in_0( data_in ),
    .in_1( s4 ),
    .out( data_out )
  );
  assign valid_out = (s2 | valid_in);
  assign addr_out = addr_in;
  assign ready_out = ready_in;
endmodule

module debugbus (
  input db_ready,
  input clock,
  input [15:0] ctrl,
  input [15:0] wrval,
  input [15:0] result,
  input [15:0] L,
  input [15:0] R,
  input [15:0] imm,
  input [15:0] pc,
  input [15:0] fullop,
  input [15:0] rdf,
  input [15:0] rsf,
  output [25:0] db
);
  wire [7:0] s0;
  wire s1;
  wire s2;
  wire [3:0] s3;
  wire [15:0] s4;
  wire s5;
  wire [7:0] s6;
  wire s7;
  wire [15:0] s8;
  wire s9;
  wire [7:0] s10;
  wire s11;
  wire s12;
  wire s13;
  wire [15:0] s14;
  wire [7:0] s15;
  assign s1 = ctrl[10];
  assign s2 = ctrl[12];
  assign s3 = rdf[3:0];
  debugregs debugregs_i0 (
    .data_in( 16'b0 ),
    .valid_in( 1'b0 ),
    .addr_in( s0 ),
    .ready_in( db_ready ),
    .write( s1 ),
    .en( s2 ),
    .clock( clock ),
    .rd( s3 ),
    .wrval( wrval ),
    .sel( 8'b0 ),
    .data_out( s4 ),
    .valid_out( s5 ),
    .addr_out( s6 ),
    .ready_out( s7 )
  );
  debugmux debugmux_i1 (
    .data_in( s4 ),
    .valid_in( s5 ),
    .addr_in( s6 ),
    .ready_in( s7 ),
    .sel( 8'b10000 ),
    .in_0( fullop ),
    .in_1( ctrl ),
    .in_2( L ),
    .in_3( R ),
    .in_4( result ),
    .in_5( wrval ),
    .in_6( imm ),
    .in_7( pc ),
    .data_out( s8 ),
    .valid_out( s9 ),
    .addr_out( s10 ),
    .ready_out( s11 )
  );
  // Addr Enumerator
  DIG_Counter_Nbit #(
    .Bits(8)
  )
  DIG_Counter_Nbit_i2 (
    .en( db_ready ),
    .C( clock ),
    .clr( s13 ),
    .out( s0 )
  );
  debugmux debugmux_i3 (
    .data_in( s8 ),
    .valid_in( s9 ),
    .addr_in( s10 ),
    .ready_in( s11 ),
    .sel( 8'b11000 ),
    .in_0( rdf ),
    .in_1( rsf ),
    .in_2( 16'b0 ),
    .in_3( 16'b0 ),
    .in_4( 16'b0 ),
    .in_5( 16'b0 ),
    .in_6( 16'b0 ),
    .in_7( 16'b0 ),
    .data_out( s14 ),
    .valid_out( s12 ),
    .addr_out( s15 )
  );
  assign s13 = ~ s12;
  assign db[15:0] = s14;
  assign db[16] = s12;
  assign db[24:17] = s15;
  assign db[25] = clock;
endmodule

module rj32 (
  input clock,
  input step,
  input run_slow,
  input [15:0] D_prog,
  input [15:0] D_in,
  input run_fast,
  input run_faster,
  input erun,
  output halt,
  output error,
  output skip,
  output stall,
  output [7:0] A_prog,
  output clock_m,
  output [13:0] A_data,
  output [15:0] D_out,
  output w_en,
  output [25:0] db
);
  wire [15:0] s0;
  wire [15:0] s1;
  wire [15:0] s2;
  wire [15:0] s3;
  wire jump_t;
  wire [3:0] rs_t;
  wire [3:0] rd_t;
  wire [15:0] L_t;
  wire [15:0] R_t;
  wire [2:0] cond_t;
  wire [4:0] op_t;
  wire rsvalid_t;
  wire rdvalid_t;
  wire [15:0] imm_t;
  wire immv_t;
  wire [2:0] aluop_t;
  wire [15:0] s4;
  wire [15:0] result_t;
  wire skip_n;
  wire [15:0] wrval_t;
  wire en;
  wire write_t;
  wire en_fetch;
  wire halt_temp;
  wire error_temp;
  wire stall_temp;
  wire skip_temp;
  wire mem_t;
  wire store_t;
  wire [1:0] wrmux_t;
  wire [15:0] s5;
  wire [15:0] s6;
  wire [15:0] ctrl_t;
  wire [15:0] fullop_t;
  wire [15:0] rdf_t;
  wire [15:0] rsf_t;
  clockctrl clockctrl_i0 (
    .clock( clock ),
    .step( step ),
    .slow( run_slow ),
    .fast( run_fast ),
    .faster( run_faster ),
    .efast( erun ),
    .stall( stall_temp )
  );
  assign clock_m = ~ clock;
  fetch fetch_i1 (
    .result( result_t ),
    .instr_i( D_prog ),
    .clock( clock ),
    .fetch( en_fetch ),
    .jump( jump_t ),
    .en( en ),
    .instr_o( s2 ),
    .pc( s3 )
  );
  control control_i2 (
    .op( op_t ),
    .stall( stall_temp ),
    .clock( clock ),
    .skip_n( skip_n ),
    .aluop( aluop_t ),
    .skip( skip_temp ),
    .halt( halt_temp ),
    .error( error_temp ),
    .en_write( en ),
    .mem( mem_t ),
    .store( store_t ),
    .en_fetch( en_fetch ),
    .jump( jump_t ),
    .write( write_t ),
    .wrmux( wrmux_t )
  );
  assign ctrl_t[0] = stall_temp;
  assign ctrl_t[1] = skip_temp;
  assign ctrl_t[2] = halt_temp;
  assign ctrl_t[3] = error_temp;
  assign ctrl_t[4] = jump_t;
  assign ctrl_t[7:5] = aluop_t;
  assign ctrl_t[8] = mem_t;
  assign ctrl_t[9] = store_t;
  assign ctrl_t[10] = write_t;
  assign ctrl_t[11] = en_fetch;
  assign ctrl_t[12] = en;
  assign ctrl_t[14:13] = wrmux_t;
  assign ctrl_t[15] = immv_t;
  writeback writeback_i3 (
    .L( s4 ),
    .result( result_t ),
    .rdval( s0 ),
    .D( D_in ),
    .wrmux( wrmux_t ),
    .wrval( wrval_t ),
    .result_out( s5 ),
    .rdval_out( s6 )
  );
  memaccess memaccess_i4 (
    .result( s5 ),
    .rdval( s6 ),
    .store( store_t ),
    .en( en ),
    .A( A_data ),
    .str( w_en ),
    .D_in( D_out )
  );
  assign A_prog = s3[7:0];
  decoder decoder_i5 (
    .rdval( s0 ),
    .rsval( s1 ),
    .instr( s2 ),
    .pc( s3 ),
    .jump( jump_t ),
    .rs( rs_t ),
    .rd( rd_t ),
    .L( L_t ),
    .R( R_t ),
    .cond( cond_t ),
    .op( op_t ),
    .rs_valid( rsvalid_t ),
    .rd_valid( rdvalid_t ),
    .imm( imm_t ),
    .immv( immv_t )
  );
  execute execute_i6 (
    .L( L_t ),
    .R( R_t ),
    .cond( cond_t ),
    .op( aluop_t ),
    .L_out( s4 ),
    .result( result_t ),
    .skip_n( skip_n )
  );
  regfile regfile_i7 (
    .rs( rs_t ),
    .rd( rd_t ),
    .wrval( wrval_t ),
    .clock( clock ),
    .en( en ),
    .wr( write_t ),
    .rdval( s0 ),
    .rsval( s1 )
  );
  debugbus debugbus_i8 (
    .db_ready( 1'b1 ),
    .clock( clock ),
    .ctrl( ctrl_t ),
    .wrval( wrval_t ),
    .result( result_t ),
    .L( L_t ),
    .R( R_t ),
    .imm( imm_t ),
    .pc( s3 ),
    .fullop( fullop_t ),
    .rdf( rdf_t ),
    .rsf( rsf_t ),
    .db( db )
  );
  assign rdf_t[3:0] = rd_t;
  assign rdf_t[4] = jump_t;
  assign rdf_t[5] = rdvalid_t;
  assign rdf_t[15:6] = 10'b0;
  assign fullop_t[4:0] = op_t;
  assign fullop_t[7:5] = cond_t;
  assign fullop_t[15:8] = 8'b0;
  assign rsf_t[3:0] = rs_t;
  assign rsf_t[4] = 1'b0;
  assign rsf_t[5] = rsvalid_t;
  assign rsf_t[15:6] = 10'b0;
  assign halt = halt_temp;
  assign error = error_temp;
  assign skip = skip_temp;
  assign stall = stall_temp;
endmodule
